div.script:
var date = new Date();
var day = date.getDate();
var month = date.getMonth() + 1;
var year = date.getFullYear();

if (month < 10) month = "0" + month;
if (day < 10) day = "0" + day;

var today = day + "." + month + "." + year 
document.getElementById("main-date").value = today;

var mobile_filter = () => {
    $('#outteamfilter-div').addClass('mobile-model-sidefilter navbarmaincont sidenav activemenu');
}

var mobile_filter_close = () => {
    $('#outteamfilter-div').removeClass('mobile-model-sidefilter navbarmaincont sidenav activemenu');
}
